import React, { useState } from "react";
import mapboxgl from 'mapbox-gl';
import subways from './data/subways.json';
import ReactMapGL, {Source, Layer, Marker, Popup} from 'react-map-gl';

const MAPBOX_TOKEN = 'pk.eyJ1IjoiYW1jMjM3NSIsImEiOiJjazJ3MnRuMXkwOHdyM25xd3V2aWp6ZWduIn0.DLl2wPIb3rDT72TZ0V7z5w';

function RenderMap2(props) {

    const [viewport, setViewport] = useState({
        width: 600,
        height: 500,
        latitude: 40.74,
        longitude: -73.9,
        zoom: 9.3
    })

    const [selectedStop, setSelectedStop] = useState(null)

    const handleCircleClick = (event) => {
        console.log("you clicked")
    }
    const subwaysLayer = {
        id: 'subways',
        type: 'circle',
        source: 'subways',
        paint: {
            'circle-color' : 'black',
            'circle-opacity': 0.2
        },
        onClick: handleCircleClick
    }


    return (
        <ReactMapGL 
            {...viewport}
            onViewportChange = {nextViewport => setViewport(nextViewport)}
            mapboxApiAccessToken = {MAPBOX_TOKEN}
            //onClick = {handleCircleClick}
            >
                {subways.features.map(stop => (
                    <Marker 
                        key = {stop.properties.GTFS_Stop_ID}
                        latitude = {stop.geometry.coordinates[1]}
                        longitude = {stop.geometry.coordinates[0]}
                        >
                        <button onClick = {(event) => {
                            event.preventDefault();
                            setSelectedStop(stop)
                            console.log(stop.properties.Stop_Name)
                        }}>
                            <img src = "/logo.svg" alt = "Circle" />
                        </button>
                    </Marker>
                ))}
                {selectedStop ? (
                    <Popup 
                        latitude = {selectedStop.geometry.coordinates[1]}
                        longitude = {selectedStop.geometry.coordinates[0]}
                        >
                            <div>
                                <h2>{selectedStop.properties.Stop_Name}</h2>
                                <p>Lines: {selectedStop.properties.Daytime_Routes}</p>
                            </div>
                    </Popup> 
                ) : null}


            </ReactMapGL>

    )
}

export default RenderMap2;
