import {Box, Card, Flex} from 'rebass'



const arr = [
            {name: "marko", id:1}, 
            {name:"anna", id : 2}
]
const arr2 = [
            {name: "marko2", id:1}, 
            {name:"anna2", id : 2}
]

function App() {

  const ListItems = ({arr}) => (arr.map(obj => <li>{obj.name}</li>))

  const [gtfs, setGtfs] = useState("test")

  const setLineStation = (gtfs_id) => (
    setGtfs(gtfs_id)
  )

console.log(gtfs)
  return (
    <div className="App">
      <Flex mx = {-2}>
        <Box width = {1/2} px = {2}>
          <RenderMap />
        </Box>
      </Flex>
      <Flex>
        <Box width = {1/2} px = {2}>
          <RenderLineChart gtfs = {gtfs}/>
        </Box>

        <Box width = {1/2} px = {2}>
          <RenderBarChart setLineStation = {setLineStation}/>
        </Box>

      </Flex>
    </div>
  );
}

export default App;
